import React, { useState } from 'react';
import axios from 'axios';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const API = axios.create({
  baseURL: 'https://localhost:7116/api',
});

export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    role: 'Student',
  });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await API.post('/auth/login', {
          email: form.email,
          password: form.password,
        });
        console.log('Login response:', res.data);

        const token = res.data.jwt; // changed this line
        localStorage.setItem('jwt', token);

        const payload = JSON.parse(atob(token.split('.')[1]));
        const role = payload['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'];

        window.location.href = role === 'Instructor' ? '/instructor-dashboard' : '/student-dashboard';
      } else {
        const user = {
          name: form.name,
          email: form.email,
          password: form.password,
          role: form.role,
        };

        const res = await API.post('/auth/register', user);

        if (res.status === 200) {
          alert('Registered successfully. You can now log in.');
          setIsLogin(true);
        }
      }
    } catch (err) {
      console.error("Login error:", err);
      alert(err.response?.data?.message || JSON.stringify(err.response?.data) || 'Authentication failed');
    }
  };

  return (
    <div className="container-fluid d-flex justify-content-center align-items-center min-vh-100 bg-light">
      <div className="card shadow p-4" style={{ maxWidth: '400px', width: '100%' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4">{isLogin ? 'Login' : 'Sign Up'}</h2>
          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <>
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Full Name</label>
                  <input
                    id="name"
                    name="name"
                    className="form-control"
                    placeholder="Enter your full name"
                    onChange={handleChange}
                    value={form.name}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="role" className="form-label">Role</label>
                  <select 
                    id="role"
                    name="role" 
                    className="form-select" 
                    value={form.role} 
                    onChange={handleChange}
                  >
                    <option value="Student">Student</option>
                    <option value="Instructor">Instructor</option>
                  </select>
                </div>
              </>
            )}
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email address</label>
              <input
                id="email"
                name="email"
                type="email"
                className="form-control"
                placeholder="Enter your email"
                onChange={handleChange}
                value={form.email}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                id="password"
                name="password"
                type="password"
                className="form-control"
                placeholder="Enter your password"
                onChange={handleChange}
                value={form.password}
                required
              />
            </div>
            <div className="d-grid gap-2">
              <button type="submit" className="btn btn-primary">
                {isLogin ? 'Login' : 'Sign Up'}
              </button>
            </div>
          </form>
          <div className="text-center mt-3">
            <p className="mb-0">
              {isLogin ? "Don't have an account?" : 'Already have an account?'}{' '}
              <button 
                type="button" 
                className="btn btn-link p-0" 
                onClick={() => setIsLogin(!isLogin)}
              >
                {isLogin ? 'Sign Up' : 'Login'}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
