// HomePage.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import './HomePage.css'; // Importing CSS for styling

const HomePage = () => {
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  };

  return (
    <div className={`home-container ${isDarkTheme ? 'dark' : 'light'}`}>
      <header className="home-header">
        <h1>Welcome to Our Learning Platform</h1>
        <p>Your journey to knowledge starts here!</p>
        <button className="theme-toggle" onClick={toggleTheme}>
          Switch to {isDarkTheme ? 'Light' : 'Dark'} Theme
        </button>
        <nav className="navbar">
          <Link to="/login" className="nav-link">Login</Link>
          <Link to="/register" className="nav-link">Register</Link>
        </nav>
      </header>

      <section className="home-features">
        <div className={`feature ${isDarkTheme ? 'dark-text bg-light' : 'light-text bg-dark'}`}>
            <h2>For Students</h2>
            <p>Access a variety of courses and resources tailored for your learning needs.</p>
        </div>

        <div className={`feature ${isDarkTheme ? 'dark-text bg-light' : 'light-text bg-dark'}`}>
            <h2>For Instructors</h2>
            <p>Share your expertise and create engaging courses for students.</p>
        </div>
      </section>

      
      <footer className="home-footer">
        <p>&copy; 2025 Learning Platform. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
